from datetime import datetime; import os; os.system("clear"); api_bot_tg = input("Your bot token: "); api_id_tg = input("Your app id: "); api_hash_tg = input("Your hash app: "); from ping3 import ping; from pyrogram import Client; app = Client("my_bot", api_id=api_id_tg, api_hash=api_hash_tg, bot_token=api_bot_tg); app.on_message()(lambda client, message: message.reply(f"{message.text} : {ping(message.text)* 1000:.2f} ms\nProject : F3Mozk1_"), print(f"[{datetime.now().strftime('%H:%M:%S.%f')[:-3]} {message.from_user.id} | {message.text} : {ping(message.text)* 1000:.2f} ms")); os.system("clear"); print("Bot started!"); app.run()
